import React, { Component } from 'react';
import './App.css';

class App extends Component {
  state = {
    recentBuses: [
      {routeId:1, headsign:"Dupa", estimatedTime:"22:23", theoreticalTime: "22:31"},
      {routeId:2, headsign:"Dupa", estimatedTime:"22:23", theoreticalTime: "22:31"}
    ],
    busstops: [1334, 1335, 151, 1874, 1846, 1340]
  };


  // 1334, 1335 wagnera
  // Suchanino : 151, 1874 ?
  // Nad jarem 1846, 1340

  componentDidMount() {
      this.state.busstops.map(busstopId => {
        this.getBusstopData(busstopId).then(newState => {

          this.setState({
            recentBuses: [...this.state.recentBuses, ...newState]
          })
        });  
      })
  }

  getBusstopData(busstopId) {
    const busstopUrl = "http://87.98.237.99:88/delays?stopId="
    return fetch(busstopUrl + busstopId, {mode: 'cors'})
    .then(response => response.json())
    .then(jsonResponse => 
      jsonResponse.delay.map((bus) => ({
          routeId: bus.routeId,
          headsign: bus.headsign,
          estimatedTime : bus.estimatedTime,
          theoreticalTime : bus.theoreticalTime
      }))
    );
  }

  render() {
    return (
      <div className="App">
        {this.state.recentBuses.map(bus => {
          return 

          <div style="border: 1px solid">
            <p>{bus.headsign}</p>
            </div>

        })}
      </div>
    );
  }
}

export default App;
