import React from "react";
import "./Bus.css";
import Moment from 'react-moment';
import moment from 'moment';

const bus = props => {
  return (
    <div className="Bus">
      <p>
        Numer autobusu: {props.routeId} Kierunek: {props.headsign}
      </p>
      Data:{moment(new Date()).format("DD/MM/YYYY")}
      Odjazd: <Moment fromNow ago> {props.estimatedTime}</Moment>
    </div>
  );
};

const getFullDate = (est) => {
    moment(new Date()).format("DD/MM/YYYY") + " " + est;
}

export default bus;
