import React, { Component } from 'react';
import './App.css';

class App extends Component {
  state = {
    recentBuses: []
  }


  // 1334, 1335 wagnera
  // Suchanino : 151, 1874 ?
  // Nad jarem 1846, 1340

  constructor(props) {
    super(props);
    this.busstop= "";
  }

  componentDidMount() {
    this.busstop = this.getBusstopData(1334);
    // this.busstop = this.getBusstopData(151);
    // this.busstop = this.getBusstopData(1846);
  }

  getBusstopData(busstopId) {
    const busstopUrl = "http://87.98.237.99:88/delays?stopId="
    fetch(busstopUrl + busstopId, {mode: 'cors'})
    .then(response => response.json())
    .then(responseInJson => {

      const busStopRecentBuses = [];
      responseInJson.delay.map(bus => {
        this.setState(prevState => ({
          recentBuses: [...prevState.recentBuses, bus]
        }))
      });
      });
  }

  render() {
    return (
      <div className="App">
       <p>{this.state.recentBuses}</p>
      </div>
    );
  }
}

export default App;
